import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Estados, Service } from './app.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  providers: [Service]
})

export class AppComponent implements OnInit{
  title = 'ClimaTempo';
  estado: Estados[];
  data: any;

  public clima: Observable<any>

  constructor(private http: HttpClient){

  }
  ngOnInit(){
    this.getClimaData();
    console.log(this.ClimaData)
  }

  getClimaData(){
    let data = JSON.parse('[{"id":3477,"name":"S\u00e3o Paulo","state":"SP","country":"BR  "}]');
    this.setClimaData(data);
  }

  setClimaData(data){
    this.ClimaData = data;
  }

}


<nav class="navbar navbar-light bg-light">
  <div class="container-fluid">
    <a class="navbar-brand">Navbar</a>
    <form class="d-flex">
      <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search">
      <dx-button class="btn btn-outline-success" type="submit">Search</dx-button>
    </form>
  </div>
</nav>
<div class="container-md">
  <div class="col">
    <dx-data-grid>
      <dxi-column
          [editorOptions]="{ width: 100 }"> 
          <tbody *ngIf="clima$ | async as clim" >
            <tr *ngFor="let city of clima.results">
            <td>{{name.city}}</td>
            </tr>
          </tbody>
      </dxi-column>
  </dx-data-grid>
  </div>
</div>
